pragma solidity 0.8.17;

// Chainlink
import { IRouterClient } from '@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol';
import { CCIPReceiver } from '@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol';
import { Client } from '@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol';
// OpenZeppelin
import { Address } from '@openzeppelin/contracts/utils/Address.sol';
import { ReentrancyGuard } from '@openzeppelin/contracts/security/ReentrancyGuard.sol';
// Interfaces
import { IIdentity } from '../interface/IIdentity.sol';
import { Gateway } from '../gateway/Gateway.sol';
import '../factory/IIdFactory.sol';

/// @notice REmOVE in PROD
import 'hardhat/console.sol';

contract CrossChainBridge is CCIPReceiver, ReentrancyGuard {
  /// @notice REMOVE for prod
  string public ccipMessage;

  address immutable i_router;
  address public idFactoryAddress;

  // Map to store the messageIds of the messages sent
  mapping(bytes32 => bool) public messageIds;

  // Map to store allowedContracts
  mapping(address => bool) public isAllowedContract;

  // Map to store onlyManagers
  mapping(address => bool) public isManager;

  // Define the enum
  enum AccessAddressTypes {
    CONTRACT,
    MANAGER
  }

  modifier onlyAllowedSender {
    require(
      isAllowedContract[msg.sender] == true || isManager[msg.sender] == true,
      'Permissions: Sender is not a allowed'
    );
    _;
  }

  modifier onlyAllowedContract() {
    require(isAllowedContract[msg.sender] == true, 'Permissions: Sender is not a allowed contract');
    _;
  }

  modifier onlyManager() {
    require(isManager[msg.sender] == true, 'Permissions: Sender is not a Manager');
    _;
  }

  modifier onlyAllowedIdentity(address _identity) {
    IIdFactory idFactory = IIdFactory(idFactoryAddress);
    require(idFactory.identityIsCreated(_identity), 'Permissions: idFactory marks this address as not identity');
    _;
  }

  event AllowedAddress(address indexed _address, uint64 indexed _type, bool indexed _status);

  event MessageSent(bytes32 indexed messageId);
  event MessageReceived(
    bytes32 messageId,
    uint64 indexed sourceChainSelector,
    address indexed sender,
    string indexed action
  );
  event IdFactoryUpdated(address indexed sender, address indexed newAddress);

  constructor(address _router) CCIPReceiver(_router) {
    i_router = _router;
    isManager[msg.sender] = true;
    emit AllowedAddress(msg.sender, uint64(AccessAddressTypes.MANAGER), true);
  }

  receive() external payable {}

  function setFactoryAddress(address _idFactoryAddress) external onlyManager {
    idFactoryAddress = _idFactoryAddress;
    emit IdFactoryUpdated(msg.sender, _idFactoryAddress);
  }

  function sendAddClaim(
    uint64 destinationChainSelector,
    address receiver,
    uint256 topic,
    uint256 scheme,
    address issuer,
    bytes memory signature,
    bytes memory data,
    string memory uri
  ) external onlyAllowedIdentity(msg.sender) {
    bytes memory _payload = abi.encode(msg.sender, topic, scheme, issuer, signature, data, uri);
    bytes memory metaPayload = abi.encode('AddClaim', _payload);
    _sendMessage(destinationChainSelector, receiver, metaPayload);
  }

  function sendRemoveClaim(
    uint64 destinationChainSelector,
    address receiver,
    address identity,
    bytes32 claimId
  ) external onlyAllowedIdentity(msg.sender) {
    bytes memory payload = abi.encode('RemoveClaim', identity, claimId);
    _sendMessage(destinationChainSelector, receiver, payload);
  }

  function sendAddKey(
    uint64 destinationChainSelector,
    address receiver,
    bytes32 key,
    uint256 purpose,
    uint256 keyType
  ) external onlyAllowedIdentity(msg.sender) {
    bytes memory _payload = abi.encode(msg.sender, key, purpose, keyType);
    bytes memory metaPayload = abi.encode('AddKey', _payload);
    _sendMessage(destinationChainSelector, receiver, metaPayload);
  }

  function sendRemoveKey(
    uint64 destinationChainSelector,
    address receiver,
    address identity,
    bytes32 key,
    uint256 purpose
  ) external onlyAllowedIdentity(msg.sender) {
    bytes memory payload = abi.encode('RemoveKey', identity, key, purpose);
    _sendMessage(destinationChainSelector, receiver, payload);
  }

  function sendCreateIdentity(
    uint64 destinationChainSelector,
    address receiver,
    address gateway,
    address identityOwner,
    string memory salt,
    bytes32[] calldata managementKeys
  ) external onlyAllowedSender {
    bytes memory _payload = abi.encode(gateway, identityOwner, salt, managementKeys);
    bytes memory metaPayload = abi.encode('CreateIdentity', _payload);
    _sendMessage(destinationChainSelector, receiver, metaPayload);
  }

  function _sendMessage(uint64 destinationChainSelector, address receiver, bytes memory payload) internal nonReentrant {
    (uint256 fee, Client.EVM2AnyMessage memory message) = calculateFeeAndMessage(
      destinationChainSelector,
      receiver,
      payload
    );

    bytes32 messageId = IRouterClient(i_router).ccipSend{ value: fee }(destinationChainSelector, message);

    emit MessageSent(messageId);
  }

  function _ccipReceive(Client.Any2EVMMessage memory message) internal override {
    // Check if the message was already processed
    require(!messageIds[message.messageId], 'Message already processed');
    messageIds[message.messageId] = true;
    // Decode the message data
    (string memory action, bytes memory _payload) = abi.decode(message.data, (string, bytes));
    if (keccak256(bytes(action)) == keccak256(bytes('AddClaim'))) {
        (
          address targetContract,
          uint256 topic,
          uint256 scheme,
          address issuer,
          bytes memory signature,
          bytes memory decodedData,
          string memory uri
        ) = abi.decode(_payload, (address, uint256, uint256, address, bytes, bytes, string));
        IIdentity(targetContract).addClaim(topic, scheme, issuer, signature, decodedData, uri);
      // } else if (keccak256(bytes(action)) == keccak256(bytes('RemoveClaim'))) {
      //   bytes32 claimId = abi.decode(data, (bytes32));
      //   IIdentity(targetContract).removeClaim(claimId);
    } else if (keccak256(bytes(action)) == keccak256(bytes('AddKey'))) {
      (address targetIdentity, bytes32 key, uint256 purpose, uint256 keyType) = abi.decode(
        _payload,
        (address, bytes32, uint256, uint256)
      );
      IIdentity(targetIdentity).addKey(key, purpose, keyType);
      // } else if (keccak256(bytes(action)) == keccak256(bytes('RemoveKey'))) {
      //   (bytes32 key, uint256 purpose) = abi.decode(data, (bytes32, uint256));
      //   IIdentity(targetContract).removeKey(key, purpose);
    } else if (keccak256(bytes(action)) == keccak256(bytes('CreateIdentity'))) {
      (address targetContract, address identityOwner, string memory salt, bytes32[] memory managementKeys) = abi.decode(
        _payload,
        (address, address, string, bytes32[])
      );
      Gateway(targetContract).deployIdentity(identityOwner, salt, managementKeys);
    } else {
      revert('Unknown action');
    }
    emit MessageReceived(message.messageId, message.sourceChainSelector, abi.decode(message.sender, (address)), action);
  }

  // Function to set allowed contracts
  function setAllowedContract(address _contract, bool _status) external onlyManager {
    if (_status == true) {
      require(Address.isContract(_contract), 'Permissions: Address is not a contract');
    }
    isAllowedContract[_contract] = _status;

    emit AllowedAddress(_contract, uint64(AccessAddressTypes.CONTRACT), _status);
  }

  // Function to set manager status
  function setManager(address _manager, bool _status) external onlyManager {
    isManager[_manager] = _status;

    emit AllowedAddress(_manager, uint64(AccessAddressTypes.MANAGER), _status);
  }

  /// @notice REMOVE for prod
  function testSendMessage(
    uint64 _destinationChainSelector,
    address _receiver,
    string memory _message
  ) public onlyAllowedSender {
    bytes memory payload = abi.encode(_message);
    _sendMessage(_destinationChainSelector, _receiver, payload);
  }

  /// @notice REMOVE for prod
  function _testRecieveMessage(Client.Any2EVMMessage memory any2EvmMessage) internal {
    bytes32 s_lastReceivedMessageId = any2EvmMessage.messageId; // fetch the messageId
    ccipMessage = abi.decode(any2EvmMessage.data, (string)); // abi-decoding of the sent text
    emit MessageReceived(s_lastReceivedMessageId, 0, msg.sender, ccipMessage);
  }

  function calculateFeeAndMessage(
    uint64 destinationChainSelector,
    address receiver,
    bytes memory payload
  ) public view returns (uint256 fee, Client.EVM2AnyMessage memory message) {
    message = Client.EVM2AnyMessage({
      receiver: abi.encode(receiver),
      data: payload,
      tokenAmounts: new Client.EVMTokenAmount[](0),
      extraArgs: Client._argsToBytes(Client.EVMExtraArgsV1({ gasLimit: 1000000 })),
      feeToken: address(0) // Use native token
    });
    fee = IRouterClient(i_router).getFee(destinationChainSelector, message);
    return (fee, message);
  }

  // Function to withdraw native token from the contract
  function withdraw(address _to, uint256 _amount) external onlyManager nonReentrant {
    require(address(this).balance >= _amount, 'Insufficient balance');
    Address.sendValue(payable(_to), _amount);
  }
}
","keccak256":"0x1fc30946adccab3c80df13ecaf639dfbfac4daa3a5882b7e8f4aca96a8ab59ae","license":"GPL-3.0"},"src/factory/IIdFactory.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

interface IIdFactory {
  /// events

  // event emitted whenever a single contract is deployed by the factory
  event Deployed(address indexed _addr);

  // event emitted when a wallet is linked to an ONCHAINID contract
  event WalletLinked(address indexed wallet, address indexed identity);

  // event emitted when a token is linked to an ONCHAINID contract
  event TokenLinked(address indexed token, address indexed identity);

  // event emitted when a wallet is unlinked from an ONCHAINID contract
  event WalletUnlinked(address indexed wallet, address indexed identity);

  // event emitted when an address is registered on the factory as a Token
  // factory address, granting this address the privilege to issue
  // Onchain identities for tokens
  event TokenFactoryAdded(address indexed factory);

  // event emitted when a previously recorded token factory address is removed
  event TokenFactoryRemoved(address indexed factory);

  // event emitted when a receiver is added for a chainSelector
  event ReceiverAdded(uint64 chainSelector, address receiver, address gateway);

  // event emitted when a receiver is removed for a chainSelector
  event ReceiverRemoved(uint64 chainSelector);

  /// functions

  /**
   *  @dev function used to create a new Identity proxy from the factory
   *  @param _wallet the wallet address of the primary owner of this ONCHAINID contract
   *  @param _salt the salt used by create2 to issue the contract
   *  requires a new salt for each deployment
   *  _wallet cannot be linked to another ONCHAINID
   *  only Owner can call => Owner is supposed to be a smart contract, managing the accessibility
   *  of the function, including calls to oracles for multichain
   *  deployment security (avoid identity theft), defining payment requirements, etc.
   */
  function createIdentity(address _wallet, string memory _salt) external returns (address);

  /**
   *  @dev function used to create a new Identity proxy from the factory, setting the wallet and listed keys as
   * MANAGEMENT keys.
   *  @param _wallet the wallet address of the primary owner of this ONCHAINID contract
   *  @param _salt the salt used by create2 to issue the contract
   *  @param _managementKeys A list of keys hash (keccak256(abiEncoded())) to add as MANAGEMENT keys.
   *  requires a new salt for each deployment
   *  _wallet cannot be linked to another ONCHAINID
   *  only Owner can call => Owner is supposed to be a smart contract, managing the accessibility
   *  of the function, including calls to oracles for multichain
   *  deployment security (avoid identity theft), defining payment requirements, etc.
   */
  function createIdentityWithManagementKeys(
    address _wallet,
    string memory _salt,
    bytes32[] memory _managementKeys
  ) external returns (address);

  /**
   *  @dev function used to create a new Token Identity proxy from the factory
   *  @param _token the address of the token contract
   *  @param _tokenOwner the owner address of the token
   *  @param _salt the salt used by create2 to issue the contract
   *  requires a new salt for each deployment
   *  _token cannot be linked to another ONCHAINID
   *  only Token factory or owner can call (owner should only use its privilege
   *  for tokens not issued by a Token factory onchain
   */
  function createTokenIdentity(address _token, address _tokenOwner, string memory _salt) external returns (address);

  /**
   * @dev function used to update bridge contract address
   * @param _bridge the address of the bridge contract
   * can be called only by Owner
   */

  function setBridge(address _bridge) external;

  /**
   *  @dev function used to link a new wallet to an existing identity
   *  @param _newWallet the address of the wallet to link
   *  requires msg.sender to be linked to an existing onchainid
   *  the _newWallet will be linked to the same OID contract as msg.sender
   *  _newWallet cannot be linked to an OID yet
   *  _newWallet cannot be address 0
   *  cannot link more than 100 wallets to an OID, for gas consumption reason
   */
  function linkWallet(address _newWallet) external;

  /**
   *  @dev function used to unlink a wallet from an existing identity
   *  @param _oldWallet the address of the wallet to unlink
   *  requires msg.sender to be linked to the same onchainid as _oldWallet
   *  msg.sender cannot be _oldWallet to keep at least 1 wallet linked to any OID
   *  _oldWallet cannot be address 0
   */
  function unlinkWallet(address _oldWallet) external;

  /**
   *  @dev function used to register an address as a token factory
   *  @param _factory the address of the token factory
   *  can be called only by Owner
   *  _factory cannot be registered yet
   *  once the factory has been registered it can deploy token identities
   */
  function addTokenFactory(address _factory) external;

  /**
   *  @dev function used to unregister an address previously registered as a token factory
   *  @param _factory the address of the token factory
   *  can be called only by Owner
   *  _factory has to be registered previously
   *  once the factory has been unregistered it cannot deploy token identities anymore
   */
  function removeTokenFactory(address _factory) external;

  /**
   *  @dev function used to add a receiver for a chainSelector
   *  @param _chainSelector the chainSelector for which the receiver is added
   *  @param _receiver the address of the receiver
   *  @param _gateway the address of the gateway
   *  can be called only by Owner
   *  _receiver cannot be registered yet
   *  once the receiver has been registered it can receive messages from the chainSelector
   */
  function addReceiver(uint64 _chainSelector, address _receiver, address _gateway) external;

  /**
   *  @dev function used to remove a receiver for a chainSelector
   *  @param _chainSelector the chainSelector for which the receiver is removed
   *  can be called only by Owner
   *  _receiver has to be registered previously
   *  once the receiver has been removed it cannot receive messages from the chainSelector anymore
   */
  function removeReceiver(uint64 _chainSelector) external;

  /**
   *  @dev getter for OID contract corresponding to a wallet/token
   *  @param _wallet the wallet/token address
   */
  function getIdentity(address _wallet) external view returns (address);

  /**
   *  @dev getter to fetch the array of wallets linked to an OID contract
   *  @param _identity the address of the OID contract
   *  returns an array of addresses linked to the OID
   */
  function getWallets(address _identity) external view returns (address[] memory);

  /**
   *  @dev getter to fetch the token address linked to an OID contract
   *  @param _identity the address of the OID contract
   *  returns the address linked to the OID
   */
  function getToken(address _identity) external view returns (address);

  /**
   *  @dev getter to know if an address is registered as token factory or not
   *  @param _factory the address of the factory
   *  returns true if the address corresponds to a registered factory
   */
  function isTokenFactory(address _factory) external view returns (bool);

  /**
   *  @dev getter to know if a salt is taken for the create2 deployment
   *  @param _salt the salt used for deployment
   */
  function isSaltTaken(string calldata _salt) external view returns (bool);

  /**
   * @dev getter for the implementation authority used by this factory.
   */
  function implementationAuthority() external view returns (address);

  /**
   * @dev getter for the receiver of a chainSelector
   * @param _chainSelector the chainSelector for which the receiver is fetched
   */
  function getReceiver(uint64 _chainSelector) external view returns (address);

  /**
   * @dev getter for the list of chainSelectors
   */
  function getChainSelectors() external view returns (uint64[] memory);

  /**
   * @dev getter for the list of receivers
   */
  function getReceivers() external view returns (address[] memory);

  /**
   * @dev getter for the bridge contract address
   */
  function bridge() external view returns (address);

  /**
   * @dev getter for the created Identites
   */
  function identityIsCreated(address identity) external view returns (bool);
}
","keccak256":"0xd9d4887eb74df24e0ee2a0300d579b0b48ddab6f71316a3a21b3ab87b8a1a300","license":"GPL-3.0"},"src/factory/IdFactory.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import '../proxy/IdentityProxy.sol';
import './IIdFactory.sol';
import '../interface/IERC734.sol';
import { CrossChainBridge } from '../bridge/Bridge.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import { Address } from '@openzeppelin/contracts/utils/Address.sol';
/// @notice REMOVE in prod
import 'hardhat/console.sol';

contract IdFactory is IIdFactory, Ownable {
  mapping(address => bool) private _tokenFactories;

  // address of the _implementationAuthority contract making the link to the implementation contract
  address private immutable _implementationAuthority;

  // as it is not possible to deploy 2 times the same contract address, this mapping allows us to check which
  // salt is taken and which is not
  mapping(string => bool) private _saltTaken;

  // ONCHAINID of the wallet owner
  mapping(address => address) private _userIdentity;

  // wallets currently linked to an ONCHAINID
  mapping(address => address[]) private _wallets;

  // ONCHAINID of the token
  mapping(address => address) private _tokenIdentity;

  // token linked to an ONCHAINID
  mapping(address => address) private _tokenAddress;

  // flag to check if the contract is on the home chain
  bool public _isHomeChain;

  // DetinationChainSelectors and Receivers address (Receiver is the Bridge contract on the destination chain)
  mapping(uint64 => address) public destinationChainSelectorToReceiver;
  mapping(uint64 => address) public destinationChainSelectorToGateway;
  uint64[] private chainSelectors;

  // CrossChainBridge address
  address public bridge;

  // Map to store allowedContracts
  mapping(address => bool) public isAllowedContract;

  // Map to store onlyManagers
  mapping(address => bool) public isManager;

  // Map to store created identities
  mapping(address => bool) public isCreatedIdentity;

  // Define the enum
  enum AccessAddressTypes {
    CONTRACT,
    MANAGER
  }

  modifier onlyAllowedSender() {
    require(
      isAllowedContract[msg.sender] == true || isManager[msg.sender] == true,
      'Permissions: Sender is not a allowed'
    );
    _;
  }

  modifier onlyAllowedContract() {
    require(isAllowedContract[msg.sender] == true, 'Permissions: Sender is not a allowed contract');
    _;
  }

  modifier onlyManager() {
    require(isManager[msg.sender] == true, 'Permissions: Sender is not a Manager');
    _;
  }

  event AllowedAddress(address indexed _address, uint64 indexed _type, bool indexed _status);

  // setting
  constructor(address _owner, address implementationAuthority, bool isHomeChain) {
    require(implementationAuthority != address(0), 'invalid argument - zero address');
    require(_owner != address(0), 'invalid argument - zero address');

    _implementationAuthority = implementationAuthority;
    _isHomeChain = isHomeChain;
    isManager[_owner] = true;
    _transferOwnership(_owner);
    emit AllowedAddress(_owner, uint64(AccessAddressTypes.MANAGER), true);
  }

  /**
   *  @dev See {IdFactory-addTokenFactory}.
   */
  function addTokenFactory(address _factory) external override onlyOwner {
    require(_factory != address(0), 'invalid argument - zero address');
    require(!isTokenFactory(_factory), 'already a factory');
    _tokenFactories[_factory] = true;
    emit TokenFactoryAdded(_factory);
  }

  /**
   *  @dev See {IdFactory-removeTokenFactory}.
   */
  function removeTokenFactory(address _factory) external override onlyOwner {
    require(_factory != address(0), 'invalid argument - zero address');
    require(isTokenFactory(_factory), 'not a factory');
    _tokenFactories[_factory] = false;
    emit TokenFactoryRemoved(_factory);
  }

  /**
   *  @dev See {IdFactory-createIdentity}.
   */
  function createIdentity(
    address _wallet,
    string memory _salt
  ) external override onlyAllowedSender returns (address identity) {
    require(_wallet != address(0), 'invalid argument - zero address');
    require(keccak256(abi.encode(_salt)) != keccak256(abi.encode('')), 'invalid argument - empty string');
    require(!_saltTaken[_salt], 'salt already taken');
    require(_userIdentity[_wallet] == address(0), 'wallet already linked to an identity');
    identity = _deployIdentity(_salt, _implementationAuthority, _wallet);
    _saltTaken[_salt] = true;
    _userIdentity[_wallet] = identity;
    _wallets[identity].push(_wallet);
    emit WalletLinked(_wallet, identity);

    isCreatedIdentity[identity] = true;
    if (_isHomeChain == true) {
      bytes32[] memory _keys;
      _bridgeCreateIdentity(_wallet, _salt, _keys);
    }
    return identity;
  }

  /**
   *  @dev See {IdFactory-createIdentityWithManagementKeys}.
   */
  function createIdentityWithManagementKeys(
    address _wallet,
    string memory _salt,
    bytes32[] memory _managementKeys
  ) external override onlyOwner returns (address) {
    require(_wallet != address(0), 'invalid argument - zero address');
    require(keccak256(abi.encode(_salt)) != keccak256(abi.encode('')), 'invalid argument - empty string');
    string memory oidSalt = string.concat('OID', _salt);
    require(!_saltTaken[oidSalt], 'salt already taken');
    require(_userIdentity[_wallet] == address(0), 'wallet already linked to an identity');
    require(_managementKeys.length > 0, 'invalid argument - empty list of keys');

    address identity = _deployIdentity(oidSalt, _implementationAuthority, _wallet);

    for (uint i = 0; i < _managementKeys.length; i++) {
      require(
        _managementKeys[i] != keccak256(abi.encode(_wallet)),
        'invalid argument - wallet is also listed in management keys'
      );
      IERC734(identity).addKey(_managementKeys[i], 1, 1);
    }

    IERC734(identity).removeKey(keccak256(abi.encode(address(this))), 1);

    _saltTaken[oidSalt] = true;
    _userIdentity[_wallet] = identity;
    _wallets[identity].push(_wallet);
    emit WalletLinked(_wallet, identity);

    return identity;
  }

  /**
   *  @dev See {IdFactory-createTokenIdentity}.
   */
  function createTokenIdentity(
    address _token,
    address _tokenOwner,
    string memory _salt
  ) external override returns (address) {
    require(isTokenFactory(msg.sender) || msg.sender == owner(), 'only Factory or owner can call');
    require(_token != address(0), 'invalid argument - zero address');
    require(_tokenOwner != address(0), 'invalid argument - zero address');
    require(keccak256(abi.encode(_salt)) != keccak256(abi.encode('')), 'invalid argument - empty string');
    string memory tokenIdSalt = string.concat('Token', _salt);
    require(!_saltTaken[tokenIdSalt], 'salt already taken');
    require(_tokenIdentity[_token] == address(0), 'token already linked to an identity');
    address identity = _deployIdentity(tokenIdSalt, _implementationAuthority, _tokenOwner);
    _saltTaken[tokenIdSalt] = true;
    _tokenIdentity[_token] = identity;
    _tokenAddress[identity] = _token;
    emit TokenLinked(_token, identity);
    return identity;
  }

  /**
   *  @dev See {IdFactory-linkWallet}.
   */
  function linkWallet(address _newWallet) external override {
    require(_newWallet != address(0), 'invalid argument - zero address');
    require(_userIdentity[msg.sender] != address(0), 'wallet not linked to an identity contract');
    require(_userIdentity[_newWallet] == address(0), 'new wallet already linked');
    require(_tokenIdentity[_newWallet] == address(0), 'invalid argument - token address');
    address identity = _userIdentity[msg.sender];
    require(_wallets[identity].length < 101, 'max amount of wallets per ID exceeded');
    _userIdentity[_newWallet] = identity;
    _wallets[identity].push(_newWallet);
    emit WalletLinked(_newWallet, identity);
  }

  /**
   *  @dev See {IdFactory-unlinkWallet}.
   */
  function unlinkWallet(address _oldWallet) external override {
    require(_oldWallet != address(0), 'invalid argument - zero address');
    require(_oldWallet != msg.sender, 'cannot be called on sender address');
    require(_userIdentity[msg.sender] == _userIdentity[_oldWallet], 'only a linked wallet can unlink');
    address _identity = _userIdentity[_oldWallet];
    delete _userIdentity[_oldWallet];
    uint256 length = _wallets[_identity].length;
    for (uint256 i = 0; i < length; i++) {
      if (_wallets[_identity][i] == _oldWallet) {
        _wallets[_identity][i] = _wallets[_identity][length - 1];
        _wallets[_identity].pop();
        break;
      }
    }
    emit WalletUnlinked(_oldWallet, _identity);
  }

  /**
   *  @dev See {IdFactory-addReceiver}.
   */

  function addReceiver(uint64 _chainSelector, address _receiver, address _gateway) external override onlyOwner {
    require(_receiver != address(0), 'invalid argument - zero address');
    require(destinationChainSelectorToReceiver[_chainSelector] == address(0), 'receiver already added');
    require(destinationChainSelectorToGateway[_chainSelector] == address(0), 'gateway already added');

    destinationChainSelectorToReceiver[_chainSelector] = _receiver;
    destinationChainSelectorToGateway[_chainSelector] = _gateway;

    chainSelectors.push(_chainSelector);
    emit ReceiverAdded(_chainSelector, _receiver, _gateway);
  }

  /**
   *  @dev See {IdFactory-removeReceiver}.
   */

  function removeReceiver(uint64 _chainSelector) external override onlyOwner {
    require(destinationChainSelectorToReceiver[_chainSelector] != address(0), 'receiver not added');
    delete destinationChainSelectorToReceiver[_chainSelector];
    delete destinationChainSelectorToGateway[_chainSelector];

    uint256 length = chainSelectors.length;
    for (uint256 i = 0; i < length; i++) {
      if (chainSelectors[i] == _chainSelector) {
        chainSelectors[i] = chainSelectors[length - 1];
        chainSelectors.pop();
        break;
      }
    }
    emit ReceiverRemoved(_chainSelector);
  }

  /**
   *  @dev See {IdFactory-getChainSelectors}.
   */

  function getChainSelectors() external view override returns (uint64[] memory) {
    return chainSelectors;
  }

  /**
   *  @dev See {IdFactory-getReceiver}.
   */

  function getReceiver(uint64 _chainSelector) external view override returns (address) {
    return destinationChainSelectorToReceiver[_chainSelector];
  }

  function getGateway(uint64 _chainSelector) external view returns (address) {
    return destinationChainSelectorToGateway[_chainSelector];
  }

  /**
   *  @dev See {IdFactory-getReceivers}.
   */

  function getReceivers() external view override returns (address[] memory) {
    address[] memory receivers = new address[](chainSelectors.length);
    for (uint256 i = 0; i < chainSelectors.length; i++) {
      receivers[i] = destinationChainSelectorToReceiver[chainSelectors[i]];
    }
    return receivers;
  }

  /**
   *  @dev See {IdFactory-getIdentity}.
   */
  function getIdentity(address _wallet) external view override returns (address) {
    if (_tokenIdentity[_wallet] != address(0)) {
      return _tokenIdentity[_wallet];
    } else {
      return _userIdentity[_wallet];
    }
  }

  /**
   *  @dev See {IdFactory-isSaltTaken}.
   */
  function isSaltTaken(string calldata _salt) external view override returns (bool) {
    return _saltTaken[_salt];
  }

  /**
   *  @dev See {IdFactory-getWallets}.
   */
  function getWallets(address _identity) external view override returns (address[] memory) {
    return _wallets[_identity];
  }

  /**
   *  @dev See {IdFactory-getToken}.
   */
  function getToken(address _identity) external view override returns (address) {
    return _tokenAddress[_identity];
  }

  /**
   *  @dev See {IdFactory-isTokenFactory}.
   */
  function isTokenFactory(address _factory) public view override returns (bool) {
    return _tokenFactories[_factory];
  }

  /**
   *  @dev See {IdFactory-implementationAuthority}.
   */
  function implementationAuthority() public view override returns (address) {
    return _implementationAuthority;
  }

  /**
   *
   * @param _bridge the address of the bridge contract
   */
  function setBridge(address _bridge) external onlyOwner {
    require(_bridge != address(0), 'invalid argument - zero address');
    bridge = _bridge;
  }

  /**
   *  @dev get bridge address
   */
  function getBridge() external view returns (address) {
    return bridge;
  }

  // deploy function with create2 opcode call
  // returns the address of the contract created
  function _deploy(string memory salt, bytes memory bytecode) private returns (address) {
    bytes32 saltBytes = bytes32(keccak256(abi.encodePacked(salt)));
    address addr;
    // solhint-disable-next-line no-inline-assembly
    assembly {
      let encoded_data := add(0x20, bytecode) // load initialization code.
      let encoded_size := mload(bytecode) // load init code's length.
      addr := create2(0, encoded_data, encoded_size, saltBytes)
      if iszero(extcodesize(addr)) {
        revert(0, 0)
      }
    }
    emit Deployed(addr);
    return addr;
  }

  // function used to deploy an identity using CREATE2
  function _deployIdentity(
    string memory _salt,
    address implementationAuthority,
    address _wallet
  ) private returns (address) {
    bytes memory _code = type(IdentityProxy).creationCode;
    bytes memory _constructData = abi.encode(implementationAuthority, _wallet, address(this));
    bytes memory bytecode = abi.encodePacked(_code, _constructData);
    return _deploy(_salt, bytecode);
  }

  function _bridgeCreateIdentity(
    address _wallet,
    string memory oidSalt,
    bytes32[] memory managementKeys // uint256 signatureExpiry, // bytes calldata signature
  ) internal {
    for (uint256 i = 0; i < chainSelectors.length; i++) {
      CrossChainBridge bridgeContract = CrossChainBridge(payable(bridge));
      bridgeContract.sendCreateIdentity(
        chainSelectors[i],
        destinationChainSelectorToReceiver[chainSelectors[i]],
        destinationChainSelectorToGateway[chainSelectors[i]],
        _wallet,
        oidSalt,
        managementKeys
        // signatureExpiry,
        // signature
      );
    }
  }

  // Function to set allowed contracts
  function setAllowedContract(address _contract, bool _status) external onlyManager {
    if (_status == true) {
      require(Address.isContract(_contract), 'Permissions: Address is not a contract');
    }
    isAllowedContract[_contract] = _status;

    emit AllowedAddress(_contract, uint64(AccessAddressTypes.CONTRACT), _status);
  }

  // Function to set manager status
  function setManager(address _manager, bool _status) external onlyManager {
    isManager[_manager] = _status;

    emit AllowedAddress(_manager, uint64(AccessAddressTypes.MANAGER), _status);
  }

  function identityIsCreated(address _identity) external view returns (bool) {
    return isCreatedIdentity[_identity];
  }
}
","keccak256":"0x7842a479a5e93ff1cbef790929f31429bf092b77ef3a3eaf7be2bf3646ca552e","license":"GPL-3.0"},"src/gateway/Gateway.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/cryptography/ECDSA.sol';
import '../factory/IdFactory.sol';
// import '../bridge/Bridge.sol';

using ECDSA for bytes32;

/// A required parameter was set to the Zero address.
error ZeroAddress();
/// The maximum number of signers was reached at deployment.
error TooManySigners();
/// The signed attempted to add was already approved.
error SignerAlreadyApproved(address signer);
/// The signed attempted to remove was not approved.
error SignerAlreadyNotApproved(address signer);
/// A requested ONCHAINID deployment was requested without a valid signature while the Gateway requires one.
error UnsignedDeployment();
/// A requested ONCHAINID deployment was requested and signer by a non approved signer.
error UnapprovedSigner(address signer);
/// A requested ONCHAINID deployment was requested with a signature revoked.
error RevokedSignature(bytes signature);
/// A requested ONCHAINID deployment was requested with a signature that expired.
error ExpiredSignature(bytes signature);
/// Attempted to revoke a signature that was already revoked.
error SignatureAlreadyRevoked(bytes signature);
/// Attempted to approve a signature that was not revoked.
error SignatureNotRevoked(bytes signature);

contract Gateway is Ownable {
  IdFactory public idFactory;
  mapping(address => bool) public approvedSigners;
  mapping(bytes => bool) public revokedSignatures;

  event SignerApproved(address indexed signer);
  event SignerRevoked(address indexed signer);
  event SignatureRevoked(bytes indexed signature);
  event SignatureApproved(bytes indexed signature);

  /**
   *  @dev Constructor for the ONCHAINID Factory Gateway.
   *  @param idFactoryAddress the address of the factory to operate (the Gateway must be owner of the Factory).
   */
  constructor(address idFactoryAddress, address[] memory signersToApprove) Ownable() {
    if (idFactoryAddress == address(0)) {
      revert ZeroAddress();
    }
    if (signersToApprove.length > 10) {
      revert TooManySigners();
    }

    for (uint i = 0; i < signersToApprove.length; i++) {
      approvedSigners[signersToApprove[i]] = true;
    }

    idFactory = IdFactory(idFactoryAddress);
  }

  /**
   *  @dev Approve a signer to sign ONCHAINID deployments. If the Gateway is setup to require signature, only
   *  deployments requested with a valid signature from an approved signer will be accepted.
   *  If the gateway does not require a signature,
   *  @param signer the signer address to approve.
   */
  function approveSigner(address signer) external onlyOwner {
    if (signer == address(0)) {
      revert ZeroAddress();
    }

    if (approvedSigners[signer]) {
      revert SignerAlreadyApproved(signer);
    }

    approvedSigners[signer] = true;

    emit SignerApproved(signer);
  }

  /**
   *  @dev Revoke a signer to sign ONCHAINID deployments.
   *  @param signer the signer address to revoke.
   */
  function revokeSigner(address signer) external onlyOwner {
    if (signer == address(0)) {
      revert ZeroAddress();
    }

    if (!approvedSigners[signer]) {
      revert SignerAlreadyNotApproved(signer);
    }

    delete approvedSigners[signer];

    emit SignerRevoked(signer);
  }

  function deployIdentity(
    address identityOwner,
    string memory salt,
    bytes32[] calldata managementKeys
  ) public returns (address) {
    if (managementKeys.length == 0) {
      return deployIdentityWithSalt(identityOwner, salt);
    } else {
      return deployIdentityWithSaltAndManagementKeys(identityOwner, salt, managementKeys);
    }
  }

  /**
   *  @dev Deploy an ONCHAINID using a factory. The operation must be signed by
   *  an approved public key. This method allow to deploy an ONCHAINID using a custom salt.
   *  @param identityOwner the address to set as a management key.
   *  @param salt to use for the deployment.
   */
  function deployIdentityWithSalt(address identityOwner, string memory salt) public returns (address) {
    if (identityOwner == address(0)) {
      revert ZeroAddress();
    }

    if (!approvedSigners[msg.sender]) {
      revert UnapprovedSigner(msg.sender);
    }

    return idFactory.createIdentity(identityOwner, salt);
  }

  /**
   *  @dev Deploy an ONCHAINID using a factory. The operation must be signed by
   *  an approved public key. This method allow to deploy an ONCHAINID using a custom salt and a custom list of
   *  management keys. Note that the identity Owner address won't be added as a management keys, if this is desired,
   *  the key hash must be listed in the managementKeys array.
   *  @param identityOwner the address to set as a management key.
   *  @param salt to use for the deployment.
   *  @param managementKeys the list of management keys to add to the ONCHAINID.
   */
  function deployIdentityWithSaltAndManagementKeys(
    address identityOwner,
    string memory salt,
    bytes32[] calldata managementKeys
  ) public returns (address) {
    if (identityOwner == address(0)) {
      revert ZeroAddress();
    }

    if (!approvedSigners[msg.sender]) {
      revert UnapprovedSigner(msg.sender);
    }

    address identity = idFactory.createIdentityWithManagementKeys(identityOwner, salt, managementKeys);

    // bool isHomeChain = idFactory._isHomeChain();

    // if (isHomeChain) {
    //   address bridgeAddress = idFactory.getBridge();
    //   // Explicit conversion to payable address and then to CrossChainBridge
    //   CrossChainBridge bridge = CrossChainBridge(payable(bridgeAddress));
    //   // Get Destination Chain Selectors and their receivers
    //   address[] memory receivers = idFactory.getReceivers();
    //   uint64[] memory chainSelectors = idFactory.getChainSelectors();
    // Send the CreateIdentity message to the bridge
    //   for (uint i = 0; i < receivers.length; i++) {
    //     if (receivers[i] != address(bridge)) {
    //       bridge.sendCreateIdentity(
    //         chainSelectors[i],
    //         receivers[i],
    //         address(this),
    //         identityOwner,
    //         salt,
    //         managementKeys,
    //         signatureExpiry,
    //         signature
    //       );
    //     }
    //   }
    // }

    return identity;
  }

  /**
   *  @dev Deploy an ONCHAINID using a factory using the identityOwner address as salt.
   *  @param identityOwner the address to set as a management key.
   */
  function deployIdentityForWallet(address identityOwner) external returns (address) {
    if (identityOwner == address(0)) {
      revert ZeroAddress();
    }

    return idFactory.createIdentity(identityOwner, Strings.toHexString(identityOwner));
  }

  /**
   *  @dev Revoke a signature, if the signature is used to deploy an ONCHAINID, the deployment would be rejected.
   *  @param signature the signature to revoke.
   */
  function revokeSignature(bytes calldata signature) external onlyOwner {
    if (revokedSignatures[signature]) {
      revert SignatureAlreadyRevoked(signature);
    }

    revokedSignatures[signature] = true;

    emit SignatureRevoked(signature);
  }

  /**
   *  @dev Remove a signature from the revoke list.
   *  @param signature the signature to approve.
   */
  function approveSignature(bytes calldata signature) external onlyOwner {
    if (!revokedSignatures[signature]) {
      revert SignatureNotRevoked(signature);
    }

    delete revokedSignatures[signature];

    emit SignatureApproved(signature);
  }

  /**
   *  @dev Transfer the ownership of the factory to a new owner.
   *  @param newOwner the new owner of the factory.
   */
  function transferFactoryOwnership(address newOwner) external onlyOwner {
    idFactory.transferOwnership(newOwner);
  }

  /**
   *  @dev Call a function on the factory. Only the owner of the Gateway can call this method.
   *  @param data the data to call on the factory.
   */
  function callFactory(bytes memory data) external onlyOwner {
    (bool success, ) = address(idFactory).call(data);
    require(success, 'Gateway: call to factory failed');
  }
}
","keccak256":"0x832a8f2d69c1a7f736f00e156983a3515de8495ff1146301fd6d667ebd4e0d3c","license":"GPL-3.0"},"src/interface/IClaimIssuer.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "./IIdentity.sol";

interface IClaimIssuer is IIdentity {
	/**
	 * @dev Emitted when a claim is revoked.
	 *
	 * Specification: MUST be triggered when revoking a claim.
	 */
	event ClaimRevoked(bytes indexed signature);

	/**
	 * @dev Revoke a claim previously issued, the claim is no longer considered as valid after revocation.
	 * @notice will fetch the claim from the identity contract (unsafe).
	 * @param _claimId the id of the claim
	 * @param _identity the address of the identity contract
	 * @return isRevoked true when the claim is revoked
	 */
	function revokeClaim(bytes32 _claimId, address _identity) external returns (bool);

	/**
	 * @dev Revoke a claim previously issued, the claim is no longer considered as valid after revocation.
	 * @param signature the signature of the claim
	 */
	function revokeClaimBySignature(bytes calldata signature) external;

	/**
	 * @dev Returns revocation status of a claim.
	 * @param _sig the signature of the claim
	 * @return isRevoked true if the claim is revoked and false otherwise
	 */
	function isClaimRevoked(bytes calldata _sig) external view returns (bool);

	/**
	 * @dev Checks if a claim is valid.
	 * @param _identity the identity contract related to the claim
	 * @param claimTopic the claim topic of the claim
	 * @param sig the signature of the claim
	 * @param data the data field of the claim
	 * @return claimValid true if the claim is valid, false otherwise
	 */
	function isClaimValid(
		IIdentity _identity,
		uint256 claimTopic,
		bytes calldata sig,
		bytes calldata data
	) external view returns (bool);
}
